mapper.update:
UPDATE CS_CUST01_MT SET --고객관리 테이블 CUST_NO = #{custNo} --고객번호 , CUST_NM = #{custNm} --고객이름 , SEX_CD = #{sexCd} --성별코드 , SCAL_YN = #{scalYn} --양음력구분 , BRDY_DT = #{brdyDt} --생년월일 , MRRG_DT = #{mrrgDt} --결혼기념일 , POC_CD = #{pocCd} --직업코드 , MBL_NO = #{mblNo} --휴대폰번호 , PSMT_GRC_CD = #{psmtGrcCd} --우편물수령코드 , EMAIL = #{email} --이메일 , ADDR = #{addr} --주소 , ADDR_DTL = #{addrDtl} --상세주소 , JN_PRT_CD = #{prtCd} --가입매장 , EMAIL_RCV_YN = #{emailRcvYn} --이메일수신동의여부 , SMS_RCV_YN = #{smsRcvYn} --sms수신동의여부 , DM_RCV_YN = #{dmRcvYn} --dm수신동의여부
--고객상태코드 정상으로 변경 , CUST_SS_CD = #{custSsCd} --고객상태코드 , JS_DT = TO_CHAR(SYSDATE,'YYYYMMDD') --가입일자 오늘로 변경 , STP_DT = NULL --중지일자 초기화 , CNCL_DT = NULL --해지일자 초기화 , CNCL_CNTS = NULL --해지사유 초기화
--고객상태코드 중지로 변경 , CUST_SS_CD = #{custSsCd} --고객상태코드 , STP_DT = TO_CHAR(SYSDATE,'YYYYMMDD') --해지일자 오늘
--고객상태코드 해지로 변경 , CUST_SS_CD = #{custSsCd} --고객상태코드 , CNCL_DT = TO_CHAR(SYSDATE,'YYYYMMDD') , CNCL_CNTS = #{cnclCnts}
WHERE CUST_NO = #{custNo}
